package com.lti;

public class Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		int b=10;
		System.out.println("before swapping a ="  +a+ " b =" +b);
		a=a+b;//30
		b=a-b;//20
		a=a-b;//10
		System.out.println("after swapping a ="  +a+ " b =" +b);
	}

}
